def printData():
    global ID

    print(f"{names[0]:5}{names[1]:>8}{names[2]:>18}{names[3]:>25}{names[4]:>14}{names[5]:>9}")
    for i in range(len(ID)):
        print(f"{ID[i]:<5}{drives[i]:>8}{driven_km_drives[i]:>18}{duration_minutes_drives[i]:>25}{driving_days[i]:>14}{device[i]:>9}")

def appendRow():
    while True:
        for i in range(6):
            data = input(f"Please enter the value for {names[i]}: ")
            if i == 0:
                ID.append(data)
            elif i == 1:
                drives.append(data)
            elif i == 2:
                driven_km_drives.append(data)
            elif i == 3:
                duration_minutes_drives.append(data)
            elif i == 4:
                driving_days.append(data)
            elif i == 5:
                device.append(data)
        if input("Would you like to add another row (Y/N): ").lower() == "n":
            break
    

while True:
    print("Welcome")
    print("1) Print data to screen")
    print("2) Add row/s")
    print("X) Exit program")
    option = input("> ")
    if option == "1":
        printData()
    elif option == "2":
        appendRow()
    elif option == "X":
        break